<script type="text/javascript">
  if (window.ue) { // Check if page is instrumented
	!(function(){
	  try {      
		// This should not impact to the latency
		if(typeof(amzn) !== 'undefined' && amzn && amzn.sx && amzn.sx.utils && amzn.sx.utils.jsDepMgr) {
		  amzn.sx.utils.jsDepMgr.when('search-js-general', 'prerenderLink',  function () {
			// declare a function which registers event handler to ATF div
			function attachToAtf($, searchPageAccess){
			  function prerenderLink(url, rel, treatment) {
				// Use tags to denote that we have made a prediction. Use scopes
				// for latency information
				var treatment_name = treatment_prefix + "_" + treatment;
				ue.tag(treatment_name + "_PRE");
				uex("ld", treatment_name + "_POSTBACK", {wb:1});
				uet("bb", treatment_name + "_LATENCY", {wb:1});
				pre_rendered = true;

				// Determine if we pre-render correctly. This is done by
				// checking if the link we predicted was correct
				var prerendered_link = $("'a[href=\"" + url + "\"]'");
				prerendered_link.click(function() {
				  ue.tag(treatment_name + "_CORRECT");
				  uex("ld", treatment_name + "_LATENCY", {wb:1});
				});

				// prerender the link passed
				var link = document.createElement('link');
				link.id = rel;
				link.rel = rel;
				link.href = url;
				document.body.appendChild(link);

			  }; //prerenderLink

			  var searchLinks = searchPageAccess.asinDpLinks();
			  var searchResults = searchPageAccess.asinResults();
			  var pre_rendered = false;
			  var treatment_prefix = "PE_PRERENDER";

				// T4 algorithm - Prerender according to mouse movement
				ue.tag(treatment_prefix + "_T4");
				var userAgent = navigator.userAgent.toLowerCase();
				// Check if the user is using Chrome or IE11. Opera has Chrome
				// in its user agent so confirm that OPR is not in it
				if(userAgent.indexOf("rv:11") > 0 || (userAgent.indexOf("chrome") > 0 && userAgent.indexOf("opr") < 0)) {
				  ue.tag(treatment_prefix + "_T4_valid");
				  var results = searchPageAccess.asinResults();
				  var windowObj = $(window);

				  var distArray = []; 
				  var timeArray = [];
				  var timesMovedCloser = [];
				  var distanceScores = [];

				  var lastScrolledLeft = 0;
				  var lastScrolledTop = 0;
				  var mouse = {x:0, y:0}

				  var timingInterval;
				  var currentDiv = -1;
				  var uniquePoints = {};

				  var DISTANCE_SCORE_THRESHOLD = 4;
				  var TIME_SCORE_THRESHOLD = 0.3;
				  var POLLING_INTERVAL = 100;


				  // Calculate the distance from the current mouse location to an ASIN link
				  function calculateDistanceFromResult(link) {
					// Check if the mouse is currently directly over the link. If so return 0.
					// There are usually two instances of the link - the image and
					// the link. Calculate minimum distance considering both
					var links = $("'a[href=\"" + link + "\"]'");
					var minDistance = 1000000;

					for(var i = 0; i < links.length; i++) {
					  var obj = $(links[i]);
					  if(obj.is(":hover")) {
						return 0;
					  }
				  
					  // The offset of the link returns its top left location.
					  // Determine the distance from the top left location and
					  // the top right location. Return the minimum distance.                    
					  var distance = calculateDistanceFromPoint(obj.offset().left, obj.offset().top);                  
					  minDistance = distance < minDistance ? distance : minDistance
					  distance = calculateDistanceFromPoint(obj.offset().left + obj.width(), obj.offset().top);
					  minDistance = distance < minDistance ? distance : minDistance
					}
					return minDistance;
				  };
			  
				  function calculateDistanceFromPoint(x, y) {
					return Math.sqrt(
					  Math.pow((mouse.x - x),2) +
					  Math.pow((mouse.y - y),2)
					);
				  };

				  function generateScores() {
					// A record is kept of the unique co-ordinates visited in the
					// current div.
					uniquePoints[mouse.x + "," + mouse.y] = 1;

					for(var i = 0; i < searchLinks.length; i++) {
					  // Determine the distance between the link and the mouse
					  var curDistance = calculateDistanceFromResult(searchLinks[i]);
					  // If the mouse has moved close or is within a certain
					  // distance increment the counter
					  if(curDistance < distArray[i] || curDistance < 100) {
						timesMovedCloser[i]++;                  
					  } else if(curDistance > 200){ 
						//Otherwise reset
						timesMovedCloser[i] = 0;
					  }

					  var change = distArray[i] - curDistance;
					  var d1 = curDistance == 0 ? 1 : change/curDistance;
					  distanceScores[i] = d1 * timesMovedCloser[i];

					  // If the mouse is currently on the ith div calculate
					  // its time score and check whether we can prerender
					  if(currentDiv == i) {
						var timeScore = (Object.keys(uniquePoints).length - 1)/timeArray[currentDiv];
						if(distanceScores[currentDiv] >= DISTANCE_SCORE_THRESHOLD && timeScore >= TIME_SCORE_THRESHOLD) {
						  // Clear the timer as a link has already been prerendered
						  clearInterval(timingInterval);
						  prerenderLink(searchLinks[currentDiv].href, 'prerender', 'T2');
						}
					  }

					  distArray[i] = curDistance;
					}
				  };

				  function getResultIndex(obj) {
					for(var i = 0; i < results.length; i++) {
					  if(results[i].id == obj.id) {
						return i;
					  }
					}
					return -1;
				  };

				  // initialize arrays
				  for(var i = 0; i < searchLinks.length; i++) {
					timeArray[i] = 0;
					timesMovedCloser[i] = 0;
					distanceScores[i] = 0;
					distArray[i] = -1;
				  }

				  // Keep track of the index of the div in which the mouse is
				  // currently on
				  for(var i = 0; i < results.length; i++) {
					var obj = $(results[i]);
					obj.mouseleave(function() {
					  var index = getResultIndex(this);
					  if(index > -1) {
						timeArray[index] = 0;
					  }
					  currentDiv = -1;
					  uniquePoints = {};
					});
					obj.mouseenter(function() {
					  currentDiv = getResultIndex(this);
					  uniquePoints = {};
					});
				  }

				  // In case a search result is opened in a separate link
				  // clear the timer. 
				  for(var i = 0; i < searchLinks.length; i++) {
					$("'a[href=\"" + searchLinks[i] + "\"]'").each(function() {                        
					  $(this).click(function() {
						clearInterval(timingInterval);
					  });
					});
				  }

				  // Keep updating the current mouse position
				  $('#atfResults, #btfResults').mousemove(function(event){
					mouse.x = event.pageX;
					mouse.y = event.pageY;
				  });

				  windowObj.scroll(function(event){
					if(lastScrolledLeft != windowObj.scrollLeft()){
					  mouse.x -= lastScrolledLeft;
					  lastScrolledLeft = windowObj.scrollLeft();
					  mouse.x += lastScrolledLeft;
					}
					if(lastScrolledTop != windowObj.scrollTop()){
					  mouse.y -= lastScrolledTop;
					  lastScrolledTop = windowObj.scrollTop();
					  mouse.y += lastScrolledTop;
					}
				  });

				  // For every polling interval calculate the new scores for every
				  // link.
				  timingInterval = setInterval(function() {
					if(currentDiv > -1) {
					  timeArray[currentDiv]++;                  
					}
					generateScores();
				  }, POLLING_INTERVAL);
				}
			}
			// hook it up with ATF event for ajax
			  amznJQ.onReady('jQuery', function() {
				SPUtils.afterEvent("spATFEvent", function() {
				  attachToAtf(jQuery, SearchPageAccess);
				});
			  });
		  }); //search general
		}
	  } catch(exception) {
		if (window.ueLogError) {
		  var additionalInfo = {
			logLevel : 'ERROR',
			attribution : 'precache-dp-assets',
			message : 'Pre-render DP failed'
		  };
		  window.ueLogError(exception, additionalInfo);
		}
	  }
	})();
  }
</script>









	





